import React from 'react';
const SearchBox = ({ searchfield, searchChange })=>{
	return(
		<div className = 'pa2'>
			<input
				className = 'tc pa3 ba b--lightest-green bg-lightest-blue '
				type = 'search'
				placeholder = 'search robots'
				onChange={searchChange}
			/>
		</div>
	);
}
export default SearchBox;
